mapper.select:
SELECT MEM_ID
	, MEM_NAME
	, IS_ADMIN
FROM AIR_MEMBER
WHERE MEM_ID=#{memId}
AND MEM_PW=#{memPw}

mapper.insert:
INSERT INTO AIR_MEMBER (
	MEM_ID
	, MEM_PW
	, MEM_NAME
	, GENDER
	, BIRTH_DATE
	, COUNTRY_CODE
	, MEM_EMAIL
	, MEM_PHONE
	, MEM_ADDR
) VALUES (
	  #{memId        }
	, #{memPw        }
	, #{memName      }
	, #{gender       }
	, #{birthDate    }
	, #{countryCode  }
	, #{memEmail     }
	, #{memPhone     }
	, #{memAddr      }
)

mapper.select:
SELECT MEM_ID
FROM AIR_MEMBER
WHERE MEM_EMAIL=#{memEmail}

SELECT MEM_ID
FROM AIR_MEMBER
WHERE MEM_ID=#{memId}

mapper.update:
UPDATE AIR_MEMBER 
	SET 
	 MEM_NAME = #{memName}
	, GENDER = #{gender}
	, BIRTH_DATE = #{birthDate}
	, MEM_EMAIL = #{memEmail}
	, MEM_PHONE = #{memPhone}
	, MEM_ADDR = #{memAddr}
WHERE MEM_ID =#{memId}

mapper.insert:
INSERT INTO MEM_IMG (
	MEM_IMG_CODE
	, ORIGIN_MEM_IMG_NAME	
	, ATTACHED_MEM_IMG_NAME 	
	, MEM_ID 				
) VALUES (
	#{memImgCode             }
	, #{originMemImgName	 }
	, #{attachedMemImgName 	 }
	, #{memId 			     }
)

mapper.select:
SELECT ATTACHED_MEM_IMG_NAME
FROM MEM_IMG
WHERE MEM_ID=#{memId}

SELECT MEM_PW
FROM AIR_MEMBER
WHERE MEM_ID=#{memId}

SELECT MEM_EMAIL
FROM AIR_MEMBER
WHERE MEM_ID=#{memId}
